import React, { useState } from 'react';
import './GameCard.css';

const GameCard = ({ game }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  
  // Default game data if not provided
  const gameData = {
    id: game?.id || 'game-1',
    title: game?.title || 'Game Title',
    cover: game?.cover || 'https://via.placeholder.com/400x600?text=Game',
    year: game?.year || '2023',
    platforms: game?.platforms || ['PC', 'PS5'],
    rating: game?.rating || (Math.random() * 2 + 3).toFixed(1), // Random between 3.0 and 5.0
  };

  const handleFavoriteClick = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsFavorite(!isFavorite);
  };

  return (
    <div 
      className="game-card"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <a href={`/game/${gameData.id}`} className="game-card-link">
        <div className="game-card-image-container">
          <img 
            src={gameData.cover} 
            alt={gameData.title} 
            className="game-card-image"
          />
          
          <div className={`game-card-overlay ${isHovered ? 'visible' : ''}`}>
            <div className="game-card-info">
              <h3 className="game-card-title">{gameData.title}</h3>
              
              <div className="game-card-meta">
                <span>{gameData.year}</span>
                <span className="dot-separator"></span>
                <span>{gameData.platforms.slice(0, 2).join(', ')}</span>
              </div>
              
              <div className="game-card-rating">
                <div className="stars">
                  {[...Array(5)].map((_, i) => (
                    <svg 
                      key={i} 
                      className={`star ${i < Math.floor(gameData.rating) ? 'filled' : ''}`} 
                      viewBox="0 0 24 24" 
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M12 17.27L18.18 21L16.54 13.97L22 9.24L14.81 8.63L12 2L9.19 8.63L2 9.24L7.46 13.97L5.82 21L12 17.27Z" />
                    </svg>
                  ))}
                </div>
                <span>{gameData.rating}</span>
              </div>
            </div>
          </div>
        </div>
      </a>
      
      <button 
        className={`favorite-btn ${isFavorite ? 'active' : ''} ${isHovered ? 'visible' : ''}`}
        onClick={handleFavoriteClick}
        aria-label={isFavorite ? "Remove from favorites" : "Add to favorites"}
      >
        <svg className="heart-icon" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z" />
        </svg>
      </button>
    </div>
  );
};

export default GameCard;
