import React, { useState, useEffect } from 'react';
import Hero from '../components/Hero';
import GameCarousel from '../components/GameCarousel';
import SteamApiService from '../services/steamApi';
import './PageStyles.css';

// Fallback mock data in case of API failures
const MOCK_DATA = {
  featuredGame: {
    id: '1151640',
    title: 'Elden Ring',
    description: 'A new dark fantasy action-RPG from FromSoftware, creators of Dark Souls.',
    cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1151640/header.jpg',
    year: 2022,
    platforms: ['PC', 'PlayStation', 'Xbox'],
    rating: 4.8
  },
  trending: [
    {
      id: '1151640',
      title: 'Elden Ring',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1151640/header.jpg',
      year: 2022,
      platforms: ['PC', 'PlayStation', 'Xbox'],
      rating: 4.8
    },
    {
      id: '990080',
      title: 'Hogwarts Legacy',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/990080/header.jpg',
      year: 2023,
      platforms: ['PC', 'PlayStation', 'Xbox'],
      rating: 4.5
    },
    {
      id: '1888160',
      title: 'Final Fantasy XVI',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1888160/header.jpg',
      year: 2023,
      platforms: ['PC', 'PlayStation'],
      rating: 4.7
    }
  ],
  topRated: [
    {
      id: '292030',
      title: 'The Witcher 3',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/292030/header.jpg',
      year: 2015,
      platforms: ['PC', 'PlayStation', 'Xbox', 'Switch'],
      rating: 4.9
    },
    {
      id: '1151640',
      title: 'Elden Ring',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1151640/header.jpg',
      year: 2022,
      platforms: ['PC', 'PlayStation', 'Xbox'],
      rating: 4.8
    },
    {
      id: '1888160',
      title: 'Final Fantasy XVI',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1888160/header.jpg',
      year: 2023,
      platforms: ['PC', 'PlayStation'],
      rating: 4.7
    }
  ],
  newReleases: [
    {
      id: '1888160',
      title: 'Final Fantasy XVI',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1888160/header.jpg',
      year: 2023,
      platforms: ['PC', 'PlayStation'],
      rating: 4.7
    },
    {
      id: '1245620',
      title: 'Resident Evil 4',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/1245620/header.jpg',
      year: 2023,
      platforms: ['PC', 'PlayStation', 'Xbox'],
      rating: 4.6
    },
    {
      id: '990080',
      title: 'Hogwarts Legacy',
      cover: 'https://cdn.akamai.steamstatic.com/steam/apps/990080/header.jpg',
      year: 2023,
      platforms: ['PC', 'PlayStation', 'Xbox'],
      rating: 4.5
    }
  ]
};

const HomePage = () => {
  const [featuredGame, setFeaturedGame] = useState(null);
  const [trendingGames, setTrendingGames] = useState([]);
  const [topRatedGames, setTopRatedGames] = useState([]);
  const [newReleases, setNewReleases] = useState([]);
  
  // Section-specific loading states
  const [loadingStates, setLoadingStates] = useState({
    featured: true,
    trending: true,
    topRated: true,
    newReleases: true
  });
  
  // Section-specific error states
  const [errorStates, setErrorStates] = useState({
    featured: null,
    trending: null,
    topRated: null,
    newReleases: null
  });

  // Update loading state helper
  const updateLoadingState = (section, isLoading) => {
    setLoadingStates(prev => ({ ...prev, [section]: isLoading }));
  };

  // Update error state helper
  const updateErrorState = (section, error) => {
    setErrorStates(prev => ({ ...prev, [section]: error }));
  };

  useEffect(() => {
    // Fetch featured game
    const fetchFeaturedGame = async () => {
      updateLoadingState('featured', true);
      try {
        const featuredData = await SteamApiService.getFeaturedGame();
        setFeaturedGame(featuredData);
        updateErrorState('featured', null);
      } catch (err) {
        console.error("Error fetching featured game:", err.message);
        updateErrorState('featured', "Failed to load featured game");
        // Use fallback data
        setFeaturedGame(MOCK_DATA.featuredGame);
      } finally {
        updateLoadingState('featured', false);
      }
    };

    // Fetch trending games
    const fetchTrendingGames = async () => {
      updateLoadingState('trending', true);
      try {
        const trendingData = await SteamApiService.getTrendingGames();
        if (trendingData && trendingData.length > 0) {
          setTrendingGames(trendingData);
        }
        updateErrorState('trending', null);
      } catch (err) {
        console.error("Error fetching trending games:", err.message);
        updateErrorState('trending', "Failed to load trending games");
        // Use fallback data
        setTrendingGames(MOCK_DATA.trending);
      } finally {
        updateLoadingState('trending', false);
      }
    };

    // Fetch top rated games
    const fetchTopRatedGames = async () => {
      updateLoadingState('topRated', true);
      try {
        const topRatedData = await SteamApiService.getTopRatedGames();
        if (topRatedData && topRatedData.length > 0) {
          setTopRatedGames(topRatedData);
        }
        updateErrorState('topRated', null);
      } catch (err) {
        console.error("Error fetching top rated games:", err.message);
        updateErrorState('topRated', "Failed to load top rated games");
        // Use fallback data
        setTopRatedGames(MOCK_DATA.topRated);
      } finally {
        updateLoadingState('topRated', false);
      }
    };

    // Fetch new releases
    const fetchNewReleases = async () => {
      updateLoadingState('newReleases', true);
      try {
        const newReleasesData = await SteamApiService.getNewReleases();
        if (newReleasesData && newReleasesData.length > 0) {
          setNewReleases(newReleasesData);
        }
        updateErrorState('newReleases', null);
      } catch (err) {
        console.error("Error fetching new releases:", err.message);
        updateErrorState('newReleases', "Failed to load new releases");
        // Use fallback data
        setNewReleases(MOCK_DATA.newReleases);
      } finally {
        updateLoadingState('newReleases', false);
      }
    };

    // Fetch all data in parallel
    Promise.all([
      fetchFeaturedGame(),
      fetchTrendingGames(),
      fetchTopRatedGames(),
      fetchNewReleases()
    ]);
  }, []);

  // Check if all sections are loading
  const isAllLoading = Object.values(loadingStates).every(state => state === true);
  
  // Check if all sections have errors
  const hasAllErrors = Object.values(errorStates).every(error => error !== null);

  return (
    <main className="home-page">
      {isAllLoading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading game data...</p>
        </div>
      ) : hasAllErrors ? (
        <div className="error-container">
          <h2>Error</h2>
          <p>Failed to load any game data. Please check your internet connection and try again.</p>
          <button 
            className="retry-button"
            onClick={() => window.location.reload()}
          >
            Retry
          </button>
        </div>
      ) : (
        <>
          {loadingStates.featured ? (
            <div className="loading-section">Loading featured game...</div>
          ) : (
            <Hero game={featuredGame} />
          )}
          
          <section className="content">
            {loadingStates.trending ? (
              <div className="loading-section">Loading trending games...</div>
            ) : (
              <GameCarousel 
                title={errorStates.trending ? "Trending Now (Offline Mode)" : "Trending Now"} 
                games={trendingGames} 
              />
            )}
            
            {loadingStates.topRated ? (
              <div className="loading-section">Loading top rated games...</div>
            ) : (
              <GameCarousel 
                title={errorStates.topRated ? "Top Rated (Offline Mode)" : "Top Rated"} 
                games={topRatedGames} 
              />
            )}
            
            {loadingStates.newReleases ? (
              <div className="loading-section">Loading new releases...</div>
            ) : (
              <GameCarousel 
                title={errorStates.newReleases ? "New Releases (Offline Mode)" : "New Releases"} 
                games={newReleases} 
              />
            )}
          </section>
        </>
      )}
    </main>
  );
};

export default HomePage;
